        if (prevMove instanceof MoveResult.Valid.Alive) {
            MoveResult.Valid.Alive move = (MoveResult.Valid.Alive) prevMove;
            EntityCell newCell = gameBoard.getEntityCell(move.newPosition);
            EntityCell oldCell = gameBoard.getEntityCell(move.origPosition);
            oldCell.setEntity(gameBoard.getPlayer());
            gameBoard.getPlayer().setPosition(move.origPosition);
            newCell.setEntity(null);

