        MoveResult.Valid validMove = (MoveResult.Valid) prevMove;
        Position currentPosition = validMove.newPosition;
        Position prevPosition = validMove.origPosition;

        gameBoard.getPlayer().setOwner(gameBoard.getEntityCell(prevPosition));
        gameBoard.getEntityCell(currentPosition).setEntity(null);

        if (validMove instanceof MoveResult.Valid.Alive) {
            MoveResult.Valid.Alive aliveMove = (MoveResult.Valid.Alive) validMove;
            for (Position gemPosition : aliveMove.collectedGems) {
