            for (Position extraLifePosition : aliveMove.collectedExtraLives) {
                gameBoard.getEntityCell(extraLifePosition).setEntity(new ExtraLife());
            }
        } else if (validMove instanceof MoveResult.Valid.Dead) {
            MoveResult.Valid.Dead deadMove = (MoveResult.Valid.Dead) validMove;
            gameBoard.getEntityCell(deadMove.minePosition).setEntity(new Mine());
        }
    }
