package pa1.controller;

import pa1.model.*;

import java.util.*;

public class GameBoardController {

    private final GameBoard gameBoard;

    public GameBoardController(final GameBoard gameBoard) {
        this.gameBoard = gameBoard;
    }

    public MoveResult makeMove(final Direction direction) {
        PositionOffset offset = direction.getOffset();
        Position playerPosition = gameBoard.getPlayer().getOwner().getPosition();
        Position newPosition = playerPosition.offsetBy(offset);

        if (newPosition.row() < 0 || newPosition.row() >= gameBoard.getNumRows() ||
            newPosition.col() < 0 || newPosition.col() >= gameBoard.getNumCols()) {
            return new MoveResult.Invalid(newPosition);
        }

        EntityCell newCell = gameBoard.getEntityCell(newPosition);
        Entity entity = newCell.getEntity();

        if (entity instanceof Mine) {
            return new MoveResult.Valid.Dead(newPosition, newPosition);
        }

        List<Position> collectedGems = new ArrayList<>();
        List<Position> collectedExtraLives = new ArrayList<>();

        if (entity instanceof Gem) {
            collectedGems.add(newPosition);
        } else if (entity instanceof ExtraLife) {
            collectedExtraLives.add(newPosition);
        }

        EntityCell oldCell = gameBoard.getPlayer().getOwner();
        oldCell.setEntity(null);
        newCell.setEntity(gameBoard.getPlayer());

        return new MoveResult.Valid.Alive(newPosition, playerPosition, collectedGems, collectedExtraLives);
    }

    public void undoMove(final MoveResult prevMove) {
        if (prevMove instanceof MoveResult.Valid.Alive) {
            MoveResult.Valid.Alive aliveMove = (MoveResult.Valid.Alive) prevMove;

            EntityCell oldCell = gameBoard.getEntityCell(aliveMove.origPosition);
            EntityCell newCell = gameBoard.getEntityCell(aliveMove.newPosition);

            oldCell.setEntity(gameBoard.getPlayer());
            newCell.setEntity(null);
        }
    }
}
