                List<Position> collectedGems = new ArrayList<>();
                collectedGems.add(newPosition);

                ((EntityCell) newCell).setEntity(null);
                gameBoard.getPlayer().getOwner().setEntity(null);
                newCell.setOwner(gameBoard.getPlayer().getOwner());

                return new MoveResult.Valid.Alive(newPosition, currentPosition, collectedGems, new ArrayList<>());
            }

            if (entity instanceof ExtraLife) {
                List<Position> collectedExtraLives = new ArrayList<>();
                collectedExtraLives.add(newPosition);

                ((EntityCell) newCell).setEntity(null);
                gameBoard.getPlayer().getOwner().setEntity(null);
                newCell.setOwner(gameBoard.getPlayer().getOwner());

                return new MoveResult.Valid.Alive(newPosition, currentPosition, new ArrayList<>(), collectedExtraLives);
            }

            if (entity instanceof Mine) {
                ((EntityCell) newCell).setEntity(null);
                gameBoard.getPlayer().getOwner().setEntity(null);
                newCell.setOwner(gameBoard.getPlayer().getOwner());

