                Position position = new Position(x, y - 1);
                char c = line.charAt(x);
                Entity entity;
                switch (c) {
                    case '#':
                        entity = new Wall();
                        break;
                    case '@':
                        destinations.add(position);
                        entity = new Empty();
                        break;
                    case '.':
                        entity = new Empty();
                        break;
