import assignment.utils.StringResources;

import java.io.InputStream;
import java.util.Scanner;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

/**
 * An input engine that fetches actions from terminal input.
 */
public class TerminalInputEngine implements InputEngine {

    /**
     * The {@link Scanner} for reading input from the terminal.
     */
    private final Scanner terminalScanner;

    /**
     * @param terminalStream The stream to read terminal inputs.
     */
    public TerminalInputEngine(InputStream terminalStream) {
        this.terminalScanner = new Scanner(terminalStream);
    }

    /**
     * Fetch an action from user in terminal to process.
     *
     * @return the user action.
     */
    @Override
    public Action fetchAction() {
        // This is an example showing how to read a line from the Scanner class.
        // Feel free to change it if you do not like it.
        final var inputLine = terminalScanner.nextLine();

        // Check if the input is "exit"
        if (StringResources.EXIT_COMMAND_TEXT.equalsIgnoreCase(inputLine)) {
            return new Exit(0);
        }

        // Check if the input is "undo"
        if ("undo".equalsIgnoreCase(inputLine)) {
            return new Undo(0);
        }

        // Check if the input is a valid move command
        Pattern pattern = Pattern.compile("^([a-z]{1})$");
        Matcher matcher = pattern.matcher(inputLine);
        if (matcher.matches()) {
            char command = inputLine.charAt(0);
            switch (command) {
                case 'u':
                case 'U':
                    return new Move.Up(0);
                case 'd':
                case 'D':
                    return new Move.Down(0);
                case 'l':
                case 'L':
                    return new Move.Left(0);
                case 'r':
                case 'R':
                    return new Move.Right(0);
                default:
                    return new InvalidInput(0, "Invalid move command: " + command);
            }
        }

        // If the input does not match any of the above cases, return an InvalidInput action
        return new InvalidInput(0, "Invalid input: " + inputLine);
    }
}

