    protected ActionResult processAction(Action action) {
        if (action instanceof Exit) {
            isExitSpecified = true;
            return new ActionResult.Success(action);
        }
        if (action instanceof Undo) {
            state.undo();
            return new ActionResult.Success(action);
        }
        if (action instanceof Move) {
            Position from = state.getPlayerPositionById(action.getInitiator());
            Position to = ((Move) action).nextPosition(from);
            Entity entity = state.getEntity(to);
            if (entity instanceof Wall) {
                return new ActionResult.Failed(action, "Cannot move to the wall.");
            }
            if (entity instanceof Box) {
                Position boxDestination = ((Move) action).nextPosition(to);
                Entity boxDestinationEntity = state.getEntity(boxDestination);
                if (boxDestinationEntity instanceof Wall) {
                    return new ActionResult.Failed(action, "Cannot move the box to the wall.");
                }
                if (boxDestinationEntity instanceof Box) {
                    return new ActionResult.Failed(action, "Cannot move the box to another box.");
                }
                state.move(to, boxDestination);
            }
            state.move(from, to);
            return new ActionResult.Success(action);
        }
        return new ActionResult.Failed(action, "Invalid action.");
    }
}

