    public TerminalSokobanGame(GameState gameState, TerminalInputEngine inputEngine, TerminalRenderingEngine renderingEngine) {
        super(gameState);
        this.inputEngine = inputEngine;
        this.renderingEngine = renderingEngine;

        // Check the number of players
        Set<Position> playerPositions = gameState.getAllPlayerPositions();
        if (playerPositions.size() > 2) {
            throw new IllegalArgumentException("Terminal-based game only supports at most two players.");
        }
    }

