/**
 * A base implementation of Sokoban Game.
 */
public abstract class AbstractSokobanGame implements SokobanGame {
    protected final GameState state;

    private boolean isExitSpecified = false;

    protected AbstractSokobanGame(GameState gameState) {
        this.state = gameState;
    }

    /**
     * @return True is the game should stop running.
     * For example when the user specified to exit the game or the user won the game.
     */
    protected boolean shouldStop() {
        return isExitSpecified || state.isWin();
    }

    /**
     * @param action The action received from the user.
     * @return The result of the action.
     */
    protected ActionResult processAction(Action action) {
        if (action instanceof Exit) {
            isExitSpecified = true;
            return new ActionResult.Success(action);
        } else if (action instanceof InvalidInput) {
            return new ActionResult.Failed(action, StringResources.INVALID_INPUT_MESSAGE);
        } else if (action instanceof Move) {
            return processMoveAction((Move) action);
        } else if (action instanceof Undo) {
            return processUndoAction((Undo) action);
        } else {
            throw new ShouldNotReachException();
        }
    }

    private ActionResult processMoveAction(Move move) {
        int playerId = move.getInitiator();
        Position currentPosition = state.getPlayerPositionById(playerId);
        if (currentPosition == null) {
            return new ActionResult.Failed(move, PLAYER_NOT_FOUND);
        }

        Position nextPosition = move.nextPosition(currentPosition);
        Entity entityAtNextPosition = state.getEntity(nextPosition);

        if (entityAtNextPosition instanceof Wall) {
            return new ActionResult.Failed(move, "Cannot move into a wall.");
        } else if (entityAtNextPosition instanceof Box) {
            Position boxNextPosition = move.nextPosition(nextPosition);
            Entity entityAtBoxNextPosition = state.getEntity(boxNextPosition);

            if (entityAtBoxNextPosition instanceof Empty) {
                state.move(nextPosition, boxNextPosition);
                state.move(currentPosition, nextPosition);
                return new ActionResult.Success(move);
            } else {
                return new ActionResult.Failed(move, "Cannot move the box.");
            }
        } else if (entityAtNextPosition instanceof Empty) {
            state.move(currentPosition, nextPosition);
            return new ActionResult.Success(move);
        } else {
            throw new ShouldNotReachException();
        }
    }

    private ActionResult processUndoAction(Undo undo) {
        Optional<Integer> undoQuota = state.getUndoQuota();
        if (undoQuota.isPresent() && undoQuota.get() > 0) {
            state.undo();
            return new ActionResult.Success(undo);
        } else if (!undoQuota.isPresent()) {
            state.undo();
            return new ActionResult.Success(undo);
        } else {
            return new ActionResult.Failed(undo, UNDO_QUOTA_RUN_OUT);
        }
    }
}
