        super(coord);
        this.pointingTo = direction;
        this.type = type;
    }

    /**
     * Sets this cell as filled.
     */
    public void setFilled() {
        isFilled = true;
    }

    /**
     * <p>
     * Hint: use {@link util.PipePatterns}
     * </p>
     *
     * @return the character representation of a termination cell in game
     */
    @Override
    public char toSingleChar() {
        switch (pointingTo) {
            case UP:
                return type == Type.SOURCE ? PipePatterns.Filled.UP_ARROW : PipePatterns.Unfilled.UP_ARROW;
            case DOWN:
                return type == Type.SOURCE ? PipePatterns.Filled.DOWN_ARROW : PipePatterns.Unfilled.DOWN_ARROW;
            case LEFT:
                return type == Type.SOURCE ? PipePatterns.Filled.LEFT_ARROW : PipePatterns.Unfilled.LEFT_ARROW;
            case RIGHT:
                return type == Type.SOURCE ? PipePatterns.Filled.RIGHT_ARROW : PipePatterns.Unfilled.RIGHT_ARROW;
            default:
                throw new IllegalStateException("Unexpected value: " + pointingTo);
        }
    }

    public enum Type {
        SOURCE, SINK
    }

