            if (isFilled) {
                switch (pointingTo) {
                    case UP:
                        return PipePatterns.Filled.UP_ARROW;
                    case DOWN:
                        return PipePatterns.Filled.DOWN_ARROW;
                    case LEFT:
                        return PipePatterns.Filled.LEFT_ARROW;
                    case RIGHT:
