    @Override
    public char toSingleChar() {
        char result;
        switch (pointingTo) {
            case UP:
                result = type == Type.SOURCE ? PipePatterns.Filled.UP_ARROW : PipePatterns.Unfilled.UP_ARROW;
                break;
            case DOWN:
                result = type == Type.SOURCE ? PipePatterns.Filled.DOWN_ARROW : PipePatterns.Unfilled.DOWN_ARROW;
                break;
            case LEFT:
                result = type == Type.SOURCE ? PipePatterns.Filled.LEFT_ARROW : PipePatterns.Unfilled.LEFT_ARROW;
                break;
