                result = type == Type.SOURCE ? PipePatterns.Filled.RIGHT_ARROW : PipePatterns.Unfilled.RIGHT_ARROW;
                break;
            default:
                result = '\0';
        }
        return result;
    }

    public enum Type {
        SOURCE, SINK
