        if (input.matches("[a-zA-Z]\\d+->[a-zA-Z]\\d+")) {
            int sourceX = input.charAt(0) - 'a';
            int sourceY = Integer.parseInt(String.valueOf(input.charAt(1))) - 1;
            int destinationX = input.charAt(3) - 'a';
            int destinationY = Integer.parseInt(String.valueOf(input.charAt(4))) - 1;

            Move move = new Move(sourceX, sourceY, destinationX, destinationY);

            for (Move availableMove : availableMoves) {
                if (availableMove.equals(move)) {
                    return move;
                }
            }
