                updateScore(player, getPiece(move.getDestination()), move);
                refreshOutput();
                winner = getWinner(player, getPiece(move.getDestination()), move);
                if (winner != null) {
                    break;
                }
            }
        }
        return winner;
    }

    @Override
    public Player getWinner(Player lastPlayer, Piece lastPiece, Move lastMove) {
        if (lastPiece instanceof Knight && lastMove.getDestination().equals(configuration.getCentralPlace())) {
            return lastPlayer;
        }
        return null;
    }

    @Override
    public void updateScore(Player player, Piece piece, Move move) {
        int score = player.getScore();
        score += Math.abs(move.getSource().x() - move.getDestination().x()) + Math.abs(move.getSource().y() - move.getDestination().y());
        player.setScore(score);
    }

    @Override
    public void movePiece(Move move) {
        board[move.getDestination().x()][move.getDestination().y()] = board[move.getSource().x()][move.getSource().y()];
        board[move.getSource().x()][move.getSource().y()] = null;
    }

    @Override
    public Move[] getAvailableMoves(Player player) {
        List<Move> availableMoves = new ArrayList<>();
        for (Piece[] pieces : board) {
            for (Piece piece : pieces) {
                if (piece != null && piece.getPlayer().equals(player)) {
                    Move[] moves = piece.getAvailableMoves(this, piece.getPlayer().equals(configuration.getPlayers()[0]) ? new Place(0, 0) : new Place(board.length - 1, board.length - 1));
                    for (Move move : moves) {
                        if (board[move.getDestination().x()][move.getDestination().y()] == null) {
                            availableMoves.add(move);
                        }
                    }
                }
            }
        }
        return availableMoves.toArray(new Move[0]);
    }
}
