package assignment;

import assignment.protocol.*;

public class JesonMor extends Game {
    public JesonMor(Configuration configuration) {
        super(configuration);
    }

    @Override
    public Player start() {
        Player winner = null;
        this.numMoves = 0;
        this.board = configuration.getInitialBoard();
        this.currentPlayer = null;
        this.refreshOutput();
        while (true) {
            for (Player player : configuration.getPlayers()) {
                this.currentPlayer = player;
                Move[] availableMoves = this.getAvailableMoves(player);
                if (availableMoves.length == 0) {
                    winner = player;
                    break;
                }
                Move nextMove = player.nextMove(this, availableMoves);
                this.movePiece(nextMove);
                this.updateScore(player, this.getPiece(nextMove.getDestination()), nextMove);
                this.refreshOutput();
                winner = this.getWinner(player, this.getPiece(nextMove.getDestination()), nextMove);
                if (winner != null) {
                    break;
                }
            }
            if (winner != null) {
                System.out.println();
                System.out.println("Congratulations! ");
                System.out.printf("Winner: %s%s%s\n", winner.getColor(), winner.getName(), Color.DEFAULT);
                return winner;
            }
        }
    }

    @Override
    public Player getWinner(Player lastPlayer, Piece lastPiece, Move lastMove) {
        if (lastPiece instanceof Knight && lastMove.getDestination().equals(configuration.getCentralPlace())) {
            return lastPlayer;
        }
        return null;
    }

    @Override
    public void updateScore(Player player, Piece piece, Move move) {
        int score = player.getScore();
        int distance = Math.abs(move.getSource().x() - move.getDestination().x()) +
                Math.abs(move.getSource().y() - move.getDestination().y());
        player.setScore(score + distance);
    }

    public void movePiece(Move move) {
        Piece piece = this.getPiece(move.getSource());
        this.board[move.getSource().x()][move.getSource().y()] = null;
        this.board[move.getDestination().x()][move.getDestination().y()] = piece;
    }

    public Move[] getAvailableMoves(Player player) {
        Move[] availableMoves = new Move[0];
        for (int i = 0; i < configuration.getSize(); i++) {
            for (int j = 0; j < configuration.getSize(); j++) {
                if (board[i][j] != null && board[i][j].getPlayer().equals(player)) {
                    Move[] pieceMoves = board[i][j].getAvailableMoves(this, new Place(i, j));
                    Move[] temp = new Move[availableMoves.length + pieceMoves.length];
                    System.arraycopy(availableMoves, 0, temp, 0, availableMoves.length);
                    System.arraycopy(pieceMoves, 0, temp, availableMoves.length, pieceMoves.length);
                    availableMoves = temp;
                }
            }
        }
        return availableMoves;
    }
}
