        Player currentPlayer = game.getCurrentPlayer();
        Player destinationPiecePlayer = destinationPiece.getPlayer();
        return !currentPlayer.equals(destinationPiecePlayer); // The move is valid if the destination piece does not belong to the current player
    }

    @Override
    public String getDescription() {
        return "piece cannot be captured by another piece belonging to the same player";
    }
}

