import assignment.utils.StringResources;

import java.util.Optional;
import java.util.Set;

/**
 * A Sokoban game running in the terminal.
 */
public class TerminalSokobanGame extends AbstractSokobanGame {

    private final InputEngine inputEngine;

    private final RenderingEngine renderingEngine;

    /**
     * Create a new instance of TerminalSokobanGame.
     * Terminal-based game only support at most two players, although the assignment.game package supports up to 26 players.
     * This is only because it is hard to control too many players in a terminal-based game.
     *
     * @param gameState       The game state.
     * @param inputEngine     the terminal input engine.
     * @param renderingEngine the terminal rendering engine.
     * @throws IllegalArgumentException when there are more than two players in the map.
     */
    public TerminalSokobanGame(GameState gameState, InputEngine inputEngine, RenderingEngine renderingEngine) {
        super(gameState);
        this.inputEngine = inputEngine;
        this.renderingEngine = renderingEngine;

        // Check the number of players
        Set<Position> playerPositions = gameState.getAllPlayerPositions();
        if (playerPositions.size() > 2) {
            throw new IllegalArgumentException("Terminal-based game only support at most two players.");
        }
    }

    @Override
    public void run() {
        renderingEngine.message(StringResources.GAME_READY_MESSAGE);

        while (!shouldStop()) {
            renderingEngine.render(state);

            Action action = inputEngine.fetchAction();
            if (action == null) {
                renderingEngine.message(StringResources.INVALID_INPUT_MESSAGE);
                continue;
            }

            ActionResult result = processAction(action);
            if (result instanceof ActionResult.Failed) {
                renderingEngine.message(((ActionResult.Failed) result).getReason());
            }

            Optional<Integer> undoQuota = state.getUndoQuota();
            if (undoQuota.isPresent()) {
                int quota = undoQuota.get();
                if (quota == Integer.MAX_VALUE) {
                    renderingEngine.message(StringResources.UNDO_QUOTA_UNLIMITED);
                } else {
                    renderingEngine.message(String.format(StringResources.UNDO_QUOTA_TEMPLATE, quota));
                }
            }

            if (state.isWin()) {
                renderingEngine.message(StringResources.WIN_MESSAGE);
                break;
            }
        }

        renderingEngine.message(StringResources.GAME_EXIT_MESSAGE);
    }
}
