            Entity nextBoxEntity = state.getEntity(nextBoxPosition);

            if (nextBoxEntity instanceof Wall || nextBoxEntity instanceof Box) {
                return new ActionResult.Failed(move, StringResources.INVALID_INPUT_MESSAGE);
            } else {
                state.move(nextPosition, nextBoxPosition);
                state.move(currentPosition, nextPosition);
                return new ActionResult.Success(move);
            }
        } else if (entity instanceof Empty) {
            state.move(currentPosition, nextPosition);
            return new ActionResult.Success(move);
        } else {
            throw new ShouldNotReachException();
        }
    }

    private ActionResult processUndo(Undo undo) {
        if (state.getUndoQuota().isPresent() && state.getUndoQuota().get() <= 0) {
            return new ActionResult.Failed(undo, UNDO_QUOTA_RUN_OUT);
        }

        state.undo();
        return new ActionResult.Success(undo);
    }
}
