                Entity nextEntity = state.getEntity(nextTo);
                if (nextEntity instanceof Wall || nextEntity instanceof Box) {
                    return new ActionResult.Failed(action, StringResources.INVALID_INPUT_MESSAGE);
                } else {
                    state.move(from, to);
                    state.move(to, nextTo);
                    return new ActionResult.Success(action);
