package assignment.player;

import assignment.piece.*;
import assignment.protocol.*;

import java.util.Scanner;

public class ConsolePlayer extends Player {

    public ConsolePlayer(String name, Color color) {
        super(name, color);
    }

    public ConsolePlayer(String name) {
        this(name, Color.GREEN);
    }

    @Override
    public Move nextMove(Game game, Move[] availableMoves) {
        Scanner scanner = new Scanner(System.in);
        System.out.println(this.getName() + ", please enter your move:");
        String input = scanner.nextLine();

        String[] parts = input.split("->");
        if (parts.length != 2) {
            System.out.println("Invalid input. Please enter your move in the format 'a1->b3'.");
            return availableMoves[0];
        }

        String sourceStr = parts[0].trim();
        String destinationStr = parts[1].trim();

        if (sourceStr.length() != 2 || destinationStr.length() != 2) {
            System.out.println("Invalid input. Please enter your move in the format 'a1->b3'.");
            return availableMoves[0];
        }

        int sourceX = sourceStr.charAt(0) - 'a';
        int sourceY = sourceStr.charAt(1) - '1';
        int destinationX = destinationStr.charAt(0) - 'a';
        int destinationY = destinationStr.charAt(1) - '1';

        if (sourceX < 0 || sourceX >= game.getConfiguration().getSize() ||
            sourceY < 0 || sourceY >= game.getConfiguration().getSize() ||
            destinationX < 0 || destinationX >= game.getConfiguration().getSize() ||
            destinationY < 0 || destinationY >= game.getConfiguration().getSize()) {
            System.out.println("Invalid input. Please enter your move within the game board.");
            return availableMoves[0];
        }

        Place source = new Place(sourceX, sourceY);
        Place destination = new Place(destinationX, destinationY);

        for (Move move : availableMoves) {
            if (move.getSource().equals(source) && move.getDestination().equals(destination)) {
                return move;
            }
        }

        System.out.println("Invalid input. Please choose a valid move from the available moves.");
        return availableMoves[0];
    }
}
```

