package assignment;

import assignment.piece.Knight;
import assignment.protocol.*;

import java.util.*;

public class JesonMor extends Game {
    private Player winner;

    public JesonMor(Configuration configuration) {
        super(configuration);
        this.board = new Piece[configuration.getSize()][configuration.getSize()];
        for (int i = 0; i < configuration.getSize(); i++) {
            for (int j = 0; j < configuration.getSize(); j++) {
                board[i][j] = null;
            }
        }
        for (Piece[] pieces : configuration.getInitialBoard()) {
            for (Piece piece : pieces) {
                Place place = piece.getAvailableMoves(this, piece.getPlayer().equals(configuration.getPlayers()[0]) ? new Place(0, 0) : new Place(configuration.getSize() - 1, configuration.getSize() - 1))[0].getDestination();
                board[place.x()][place.y()] = piece;
            }
        }
    }

    @Override
    public Player start() {
        currentPlayer = configuration.getPlayers()[0];
        while (winner == null) {
            Move[] availableMoves = getAvailableMoves(currentPlayer);
            if (availableMoves.length == 0) {
                currentPlayer = currentPlayer == configuration.getPlayers()[0] ? configuration.getPlayers()[1] : configuration.getPlayers()[0];
                continue;
            }
            Move move = currentPlayer.nextMove(this, availableMoves);
            movePiece(move);
            updateScore(currentPlayer, board[move.getSource().x()][move.getSource().y()], move);
            refreshOutput();
            winner = getWinner(currentPlayer, board[move.getSource().x()][move.getSource().y()], move);
            currentPlayer = currentPlayer == configuration.getPlayers()[0] ? configuration.getPlayers()[1] : configuration.getPlayers()[0];
            numMoves++;
        }
        return winner;
    }

    @Override
    public Player getWinner(Player lastPlayer, Piece lastPiece, Move lastMove) {
        if (lastPiece instanceof Knight && Math.abs(lastMove.getDestination().x() - lastMove.getSource().x()) == 2 && Math.abs(lastMove.getDestination().y() - lastMove.getSource().y()) == 1) {
            return lastPlayer;
        }
        if (numMoves >= configuration.getNumMovesProtection()) {
            return lastPlayer;
        }
        return null;
    }

    @Override
    public void updateScore(Player player, Piece piece, Move move) {
        player.setScore(player.getScore() + Math.abs(move.getSource().x() - move.getDestination().x()) + Math.abs(move.getSource().y() - move.getDestination().y()));
    }

    @Override
    public void movePiece(Move move) {
        Place source = move.getSource();
        Place destination = move.getDestination();
        board[destination.x()][destination.y()] = board[source.x()][source.y()];
        board[source.x()][source.y()] = null;
    }

    @Override
    public Move[] getAvailableMoves(Player player) {
        List<Move> availableMoves = new ArrayList<>();
        for (int i = 0; i < configuration.getSize(); i++) {
            for (int j = 0; j < configuration.getSize(); j++) {
                Piece piece = board[i][j];
                if (piece != null && piece.getPlayer() == player) {
                    Move[] moves = piece.getAvailableMoves(this, new Place(i, j));
                    availableMoves.addAll(Arrays.asList(moves));
                }
            }
        }
        return availableMoves.toArray(new Move[0]);
    }
}
