package assignment;

import assignment.piece.Knight;
import assignment.protocol.*;

import java.util.ArrayList;
import java.util.List;

public class JesonMor extends Game {
    private Player winner;

    public JesonMor(Configuration configuration) {
        super(configuration);
        this.board = new Piece[configuration.getSize()][configuration.getSize()];
        for (int i = 0; i < configuration.getSize(); i++) {
            for (int j = 0; j < configuration.getSize(); j++) {
                this.board[i][j] = null;
            }
        }
        for (Piece[] pieces : configuration.getInitialBoard()) {
            for (Piece piece : pieces) {
                this.board[piece.getPlayer().getColor() == Color.RED ? piece.getPlayer().getColor() : piece.getPlayer().getColor()][piece.getPlayer().getColor() == Color.RED ? piece.getPlayer().getColor() : piece.getPlayer().getColor()] = piece;
            }
        }
    }

    @Override
    public Player start() {
        while (winner == null) {
            Player currentPlayer = this.currentPlayer;
            Move[] availableMoves = getAvailableMoves(currentPlayer);
            Move move = currentPlayer.nextMove(this, availableMoves);
            movePiece(move);
            updateScore(currentPlayer, this.board[move.getDestination().x()][move.getDestination().y()], move);
            refreshOutput();
            this.currentPlayer = this.currentPlayer == this.configuration.getPlayers()[0] ? this.configuration.getPlayers()[1] : this.configuration.getPlayers()[0];
            this.numMoves++;
            winner = getWinner(currentPlayer, this.board[move.getDestination().x()][move.getDestination().y()], move);
        }
        return winner;
    }

    @Override
    public Player getWinner(Player lastPlayer, Piece lastPiece, Move lastMove) {
        if (lastPiece instanceof Knight && lastMove.getDestination().x() == this.configuration.getCentralPlace().x() && lastMove.getDestination().y() == this.configuration.getCentralPlace().y()) {
            return lastPlayer;
        }
        return null;
    }

    @Override
    public void updateScore(Player player, Piece piece, Move move) {
        int score = player.getScore() + Math.abs(move.getSource().x() - move.getDestination().x()) + Math.abs(move.getSource().y() - move.getDestination().y());
        player.setScore(score);
    }

    @Override
    public void movePiece(Move move) {
        Piece piece = this.board[move.getSource().x()][move.getSource().y()];
        this.board[move.getSource().x()][move.getSource().y()] = null;
        this.board[move.getDestination().x()][move.getDestination().y()] = piece;
    }

    @Override
    public Move[] getAvailableMoves(Player player) {
        List<Move> availableMoves = new ArrayList<>();
        for (int i = 0; i < this.configuration.getSize(); i++) {
            for (int j = 0; j < this.configuration.getSize(); j++) {
                Piece piece = this.board[i][j];
                if (piece != null && piece.getPlayer() == player) {
                    Move[] moves = piece.getAvailableMoves(this, new Place(i, j));
                    for (Move move : moves) {
                        if (move.getDestination().x() >= 0 && move.getDestination().x() < this.configuration.getSize() && move.getDestination().y() >= 0 && move.getDestination().y() < this.configuration.getSize() && this.board[move.getDestination().x()][move.getDestination().y()] == null) {
                            availableMoves.add(move);
                        }
                    }
                }
            }
        }
        return availableMoves.toArray(new Move[0]);
    }
}
