public class JesonMor extends Game {
    private Player winner;

    public JesonMor(Configuration configuration) {
        super(configuration);
        this.board = new Piece[configuration.getSize()][configuration.getSize()];
        for (int i = 0; i < configuration.getSize(); i++) {
            for (int j = 0; j < configuration.getSize(); j++) {
                Place place = new Place(i, j);
                Piece piece = configuration.getInitialBoard()[i][j];
                if (piece != null) {
                    this.board[i][j] = piece;
                }
            }
        }
    }

    @Override
    public Player start() {
        for (Player player : configuration.getPlayers()) {
            while (winner == null) {
                Move[] availableMoves = getAvailableMoves(player);
                if (availableMoves.length == 0) {
                    break;
                }
                Move move = player.nextMove(this, availableMoves);
                movePiece(move);
                updateScore(player, move.getSource().getPiece(), move);
                refreshOutput();
                winner = getWinner(player, move.getSource().getPiece(), move);
                currentPlayer = configuration.getPlayers()[(configuration.getPlayers().indexOf(player) + 1) % configuration.getPlayers().length];
                numMoves++;
            }
            if (winner != null) {
                return winner;
            }
        }
        return null;
    }

    @Override
    public Player getWinner(Player lastPlayer, Piece lastPiece, Move lastMove) {
        if (lastPiece instanceof Knight && Math.abs(lastMove.getDestination().x() - lastMove.getSource().x()) == 2 && Math.abs(lastMove.getDestination().y() - lastMove.getSource().y()) == 2) {
            return lastPlayer;
        }
        return null;
    }

    @Override
    public void updateScore(Player player, Piece piece, Move move) {
        int score = player.getScore();
        score += Math.abs(move.getSource().x() - move.getDestination().x()) + Math.abs(move.getSource().y() - move.getDestination().y());
        player.setScore(score);
    }

    @Override
    public void movePiece(Move move) {
        board[move.getDestination().x()][move.getDestination().y()] = board[move.getSource().x()][move.getSource().y()];
        board[move.getSource().x()][move.getSource().y()] = null;
    }

    @Override
    public Move[] getAvailableMoves(Player player) {
        List<Move> availableMoves = new ArrayList<>();
        for (int i = 0; i < configuration.getSize(); i++) {
            for (int j = 0; j < configuration.getSize(); j++) {
                if (board[i][j] != null && board[i][j].getPlayer() == player) {
                    Place source = new Place(i, j);
                    Move[] moves = board[i][j].getAvailableMoves(this, source);
                    availableMoves.addAll(List.of(moves));
                }
            }
        }
        return availableMoves.toArray(new Move[0]);
