    /**
     * The number of moves within which capturing piece is not allowed.
     */
    private final int numProtectedMoves;

    public FirstNMovesProtectionRule(int numProtectedMoves) {
        this.numProtectedMoves = numProtectedMoves;
    }

    @Override
    public boolean validate(Game game, Move move) {
        // Get the source and destination places
        Place source = move.getSource();
        Place destination = move.getDestination();

        // Get the pieces at the source and destination places
        Piece sourcePiece = game.getPiece(source);
        Piece destinationPiece = game.getPiece(destination);

        // If the source piece is not a capturable piece or the destination piece is not null, return true
        if (sourcePiece == null || destinationPiece != null) {
            return true;
        }

        // If the number of moves is less than the number of protected moves, return true
        if (game.getNumMoves() < numProtectedMoves) {
            return true;
        }

        // If the source piece is not owned by the current player, return true
        if (!sourcePiece.getPlayer().equals(game.getCurrentPlayer())) {
            return true;
        }

        // If the source piece is a capturable piece and the number of moves is greater than or equal to the number of protected moves, return false
        return false;
    }

    @Override
    public String getDescription() {
        return "Capturing piece in the first " + this.numProtectedMoves + " moves are not allowed";
    }
}

