     */
    @Override
    public Move[] getAvailableMoves(Game game, Place source) {
        int x = source.x();
        int y = source.y();
        ArrayList<Move> validMoves = new ArrayList<>();
        int[][] possibleMoves = {
                {x + 1, y + 2},
                {x + 2, y + 1},
                {x + 2, y - 1},
                {x + 1, y - 2},
                {x - 1, y - 2},
                {x - 2, y - 1},
