        Move chosenMove = null;
        boolean validInput = false;
        while (!validInput) {
            System.out.print("Enter your move (e.g. a1->b3): ");
            String input = scanner.nextLine();
            if (input.matches("[a-h][1-8]->[a-h][1-8]")) {
                int sourceX = input.charAt(0) - 'a';
                int sourceY = input.charAt(1) - '1';
                int destX = input.charAt(4) - 'a';
                int destY = input.charAt(5) - '1';
                Place source = new Place(sourceX, sourceY);
                Place destination = new Place(destX, destY);
                chosenMove = new Move(source, destination);
                for (Move move : availableMoves) {
                    if (move.equals(chosenMove)) {
                        validInput = true;
                        break;
                    }
                }
            }
            if (!validInput) {
                System.out.println("Invalid move! Please try again.");
            }
        }
        return chosenMove;
    }
}
