    @Override
    public Player start() {
        // reset all things
        Player winner = null;
        this.numMoves = 0;
        this.board = configuration.getInitialBoard();
        this.currentPlayer = null;
        this.refreshOutput();
        while (true) {
            // get the current player
            Player player = this.getPlayers()[this.numMoves % this.getPlayers().length];
            // get available moves for the current player
            Move[] availableMoves = this.getAvailableMoves(player);
            // let the player make a move
            Move move = player.nextMove(this, availableMoves);
            // move the piece
            this.movePiece(move);
            // update the score
            this.updateScore(player, this.getPiece(move.destination()), move);
            // refresh the output
            this.refreshOutput();
            // check if there is a winner
            winner = this.getWinner(player, this.getPiece(move.destination()), move);
            if (winner != null) {
                System.out.println();
                System.out.println("Congratulations! ");
                System.out.printf("Winner: %s%s%s\n", winner.getColor(), winner.getName(), Color.DEFAULT);
