package assignment.piece;

import assignment.piece.Archer;
import assignment.piece.Rule;
import assignment.protocol.Game;
import assignment.protocol.Move;
import assignment.protocol.Piece;
import assignment.protocol.Place;
import assignment.protocol.Player;

/**
 * The rule of moving of Archer, which is similar to the moving rule of cannon in Chinese chess.
 * Cannons move like chariots, any distance orthogonally without jumping, but can only capture by jumping a single piece of either colour along the path of attack.
 */
public class ArcherMoveRule implements Rule {
    @Override
    public boolean validate(Game game, Move move) {
        if (!(game.getPiece(move.getSource()) instanceof Archer)) {
            return true;
        }
        int sourceX = move.getSource().x();
        int sourceY = move.getSource().y();
        int destX = move.getDestination().x();
        int destY = move.getDestination().y();
        // check if the move is orthogonal
        if (sourceX != destX && sourceY != destY) {
            return false;
        }
        // check if there is exactly one piece between source and destination
        int deltaX = (sourceX == destX) ? 0 : (destX - sourceX) / Math.abs(destX - sourceX);
        int deltaY = (sourceY == destY) ? 0 : (destY - sourceY) / Math.abs(destY - sourceY);
        int x = sourceX + deltaX;
        int y = sourceY + deltaY;
        int count = 0;
        while (x != destX || y != destY) {
            if (game.getPiece(x, y) != null) {
                count++;
            }
            x += deltaX;
            y += deltaY;
        }
        return count == 1;
    }

    @Override
    public String getDescription() {
        return "archer move rule is violated";
    }
}

