import assignment.piece.Rule;
import assignment.protocol.Game;
import assignment.protocol.Move;
import assignment.protocol.Piece;
import assignment.protocol.Place;

/**
 * Moving rule of Knight in chess (no block)
 * It moves two squares vertically and one square horizontally, or two squares horizontally and one square vertically
 */
public class KnightMoveRule implements Rule {

    @Override
    public boolean validate(Game game, Move move) {
        if (!(game.getPiece(move.getSource()) instanceof Knight)) {
            return true;
        }
        int sourceX = move.getSource().x();
        int sourceY = move.getSource().y();
        int destX = move.getDestination().x();
        int destY = move.getDestination().y();
        int diffX = Math.abs(destX - sourceX);
        int diffY = Math.abs(destY - sourceY);
        return (diffX == 1 && diffY == 2) || (diffX == 2 && diffY == 1);
    }

    @Override
    public String getDescription() {
        return "knight move rule is violated";
    }
}
```

