    public Move(int sourceX, int sourceY, int destinationX, int destinationY) {
        this.source = new Place(sourceX, sourceY);
        this.destination = new Place(destinationX, destinationY);
    }

    public Move(Place source, int destinationX, int destinationY) {
        this.source = source;
        this.destination = new Place(destinationX, destinationY);
    }

    /* Getters start */
    public Place getSource() {
        return source;
    }

    public Place getDestination() {
        return destination;
    }

    /* Getters end */
    /* Object methods start */
    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Move move = (Move) o;
        return source.equals(move.source) && destination.equals(move.destination);
    }

    @Override
    public int hashCode() {
        int result = source.hashCode();
        result = 31 * result + destination.hashCode();
        return result;
    }

    @Override
    public String toString() {
