import assignment.protocol.Place;
import assignment.protocol.Player;

/**
 * Global rule that requires that a piece should not go to a destination where there is another piece belonging to the
 * same player.
 */
public class OccupiedRule implements Rule {
    @Override
    public boolean validate(Game game, Move move) {
        Place destination = move.getDestination();
        Piece piece = game.getPiece(destination);
        if (piece == null) {
            return true; // destination is empty, so move is valid
        } else {
            Player currentPlayer = game.getCurrentPlayer();
            if (piece.getPlayer().equals(currentPlayer)) {
                return false; // destination is occupied by the same player's piece, so move is invalid
            } else {
                return true; // destination is occupied by opponent's piece, so move is valid
            }
        }
    }

    @Override
    public String getDescription() {
        return "piece cannot be captured by another piece belonging to the same player";
    }
}

