public static GameMap parse(String mapText) {
    String[] lines = mapText.split("\n");
    int undoLimit = Integer.parseInt(lines[0]);
    if (undoLimit < -1) {
        throw new IllegalArgumentException("Undo limit cannot be negative except for -1");
    }

    Map<Position, Entity> map = new HashMap<>();
    Set<Position> destinations = new HashSet<>();
    Map<Integer, Position> playerPositions = new HashMap<>();
    Map<Integer, Integer> boxCount = new HashMap<>();

    for (int y = 0; y < lines.length - 1; y++) {
        String line = lines[y + 1];
        for (int x = 0; x < line.length(); x++) {
            char c = line.charAt(x);
            Position position = Position.of(x, y);
            switch (c) {
                case '#':
                    map.put(position, new Wall());
                    break;
                case '@':
                    destinations.add(position);
                    map.put(position, new Empty());
                    break;
                default:
                    if (Character.isUpperCase(c)) {
                        int playerId = c - 'A';
                        if (playerPositions.containsKey(playerId)) {
                            throw new IllegalArgumentException("Multiple players with the same id");
                        }
                        playerPositions.put(playerId, position);
                        map.put(position, new Player(playerId));
                    } else if (Character.isLowerCase(c)) {
                        int playerId = Character.toUpperCase(c) - 'A';
                        if (!playerPositions.containsKey(playerId)) {
                            throw new IllegalArgumentException("No player for the box with id " + playerId);
                        }
                        int boxCountForPlayer = boxCount.getOrDefault(playerId, 0);
                        boxCount.put(playerId, boxCountForPlayer + 1);
                        map.put(position, new Box(playerId));
                    } else if (c == '.') {
                        map.put(position, new Empty());
                    } else {
                        throw new IllegalArgumentException("Invalid character in the map: " + c);
                    }
                    break;
            }
        }
    }

    if (playerPositions.isEmpty()) {
        throw new IllegalArgumentException("No players in the map");
    }

    if (boxCount.size() != destinations.size()) {
        throw new IllegalArgumentException("Number of boxes is not equal to the number of destinations");
    }

    for (int playerId : playerPositions.keySet()) {
        if (!boxCount.containsKey(playerId) || boxCount.get(playerId) != 1) {
            throw new IllegalArgumentException("Player " + (char) ('A' + playerId) + " has invalid number of boxes");
        }
    }

    return new GameMap(map, destinations, undoLimit);
}

