    String[] lines = mapText.split("\n");
    int undoLimit = Integer.parseInt(lines[0]);
    Map<Position, Entity> map = new HashMap<>();
    Set<Position> destinations = new HashSet<>();
    Map<Character, Integer> playerIds = new HashMap<>();
    AtomicInteger boxCount = new AtomicInteger(0);

    for (int y = 0; y < lines.length - 1; y++) {
        String line = lines[y + 1];
        for (int x = 0; x < line.length(); x++) {
            char c = line.charAt(x);
            Position position = Position.of(x, y);
            switch (c) {
                case '#':
                    map.put(position, new Wall());
                    break;
                case '@':
                    destinations.add(position);
                    map.put(position, new Empty());
                    break;
                case '.':
                    map.put(position, new Empty());
                    break;
                default:
                    if (Character.isUpperCase(c)) {
                        int playerId = playerIds.computeIfAbsent(c, k -> playerIds.size() + 1);
                        map.put(position, new Player(playerId));
                    } else if (Character.isLowerCase(c)) {
                        int playerId = playerIds.get(Character.toUpperCase(c));
                        map.put(position, new Box(playerId));
                        boxCount.incrementAndGet();
                    } else {
                        throw new IllegalArgumentException("Invalid character in the map: " + c);
                    }
            }
        }
    }

    if (playerIds.isEmpty()) {
        throw new IllegalArgumentException("There are no players in the map.");
    }

    if (boxCount.get() != destinations.size()) {
        throw new IllegalArgumentException("The number of boxes is not equal to the number of box destinations.");
    }

    for (Entity entity : map.values()) {
        if (entity instanceof Box) {
            int playerId = ((Box) entity).getPlayerId();
            if (!playerIds.containsValue(playerId)) {
                throw new IllegalArgumentException("There are boxes whose player id do not match any player on the game board.");
            }
        } else if (entity instanceof Player) {
            int playerId = ((Player) entity).getId();
            if (!playerIds.containsValue(playerId)) {
                throw new IllegalArgumentException("There are players that have no corresponding boxes.");
            }
        }
    }

    return new GameMap(map, destinations, undoLimit);
}

