    for (int playerId : boxCount.keySet()) {
        if (!boxCount.containsKey(playerId)) {
            throw new IllegalArgumentException("There are boxes whose playerId do not match any player on the game board.");
        }
    }

    for (int playerId : boxCount.keySet()) {
        if (!boxCount.containsKey(playerId)) {
            throw new IllegalArgumentException("There are players that have no corresponding boxes.");
        }
    }

    return new GameMap(map, destinations, undoLimit);
}
