            throw new IllegalArgumentException("No players in the map");
        }

        long boxCount = map.values().stream().filter(entity -> entity instanceof Box).count();
        if (boxCount != destinations.size()) {
            throw new IllegalArgumentException("Number of boxes does not match number of destinations");
        }

        for (Entity entity : map.values()) {
            if (entity instanceof Box) {
                Box box = (Box) entity;
                if (!players.keySet().stream().anyMatch(playerChar -> players.get(playerChar).getId() == box.getPlayerId())) {
                    throw new IllegalArgumentException("No player for box " + box.getPlayerId());
                }
            }
        }

        for (Player player : players.values()) {
            if (map.values().stream().noneMatch(entity -> entity instanceof Box && ((Box) entity).getPlayerId() == player.getId())) {
