                    map.put(position, new Wall());
                    break;
                case '.':
                    map.put(position, new Empty());
                    break;
                case '@':
                    destinations.add(position);
                    map.put(position, new Empty());
                    break;
                default:
                    if (Character.isUpperCase(symbol)) {
                        Player player = players.get(symbol);
                        if (player == null) {
                            player = new Player(symbol - 'A');
                            players.put(symbol, player);
                        }
                        map.put(position, player);
                    } else if (Character.isLowerCase(symbol)) {
                        Box box = new Box(symbol - 'a');
                        boxes.put(symbol, box);
                        map.put(position, box);
                    } else {
                        throw new IllegalArgumentException("Invalid symbol in map: " + symbol);
                    }
                    break;
            }
        }
    }

    if (players.isEmpty()) {
        throw new IllegalArgumentException("No players in the map");
    }

    if (boxes.size() != destinations.size()) {
        throw new IllegalArgumentException("Number of boxes is not equal to the number of box destinations");
    }

