public static GameMap parse(String mapText) {
    String[] lines = mapText.split("\n");
    int undoLimit = Integer.parseInt(lines[0]);
    Set<Position> destinations = new HashSet<>();
    Map<Position, Entity> map = new HashMap<>();
    AtomicInteger playerCount = new AtomicInteger(0);
    Map<Integer, Integer> boxCountByPlayerId = new HashMap<>();
    Map<Integer, Integer> boxCountByBoxId = new HashMap<>();

    for (int y = 0; y < lines.length - 1; y++) {
        String line = lines[y + 1];
        for (int x = 0; x < line.length(); x++) {
            char c = line.charAt(x);
            Position position = Position.of(x, y);
            switch (c) {
                case '#':
                    map.put(position, new Wall());
                    break;
                case '@':
                    destinations.add(position);
                    map.put(position, new Empty());
                    break;
                case '.':
                    map.put(position, new Empty());
                    break;
                default:
                    if (Character.isUpperCase(c)) {
                        int playerId = c - 'A';
                        if (playerCount.getAndIncrement() > 0) {
                            throw new IllegalArgumentException("Multiple players found in the map");
                        }
                        map.put(position, new Player(playerId));
                    } else if (Character.isLowerCase(c)) {
                        int boxId = c - 'a';
                        int playerId = Character.toUpperCase(c) - 'A';
                        boxCountByPlayerId.put(playerId, boxCountByPlayerId.getOrDefault(playerId, 0) + 1);
                        boxCountByBoxId.put(boxId, playerId);
                        map.put(position, new Box(playerId));
                    } else {
                        throw new IllegalArgumentException("Invalid character in the map");
                    }
            }
        }
    }

    if (playerCount.get() == 0) {
        throw new IllegalArgumentException("No players found in the map");
    }

    if (boxCountByPlayerId.size() != boxCountByBoxId.size()) {
        throw new IllegalArgumentException("Number of boxes does not match the number of box destinations");
    }

    for (int playerId : boxCountByPlayerId.keySet()) {
        if (!boxCountByBoxId.containsValue(playerId)) {
            throw new IllegalArgumentException("Player " + (char) ('A' + playerId) + " has no corresponding boxes");
        }
    }

    for (int boxId : boxCountByBoxId.keySet()) {
        if (!boxCountByPlayerId.containsKey(boxCountByBoxId.get(boxId))) {
            throw new IllegalArgumentException("Box " + (char) ('a' + boxId) + " does not match any player on the game board");
        }
    }

    return new GameMap(map, destinations, undoLimit);
}

