public static GameMap parse(String mapText) {
    String[] lines = mapText.split("\n");
    int undoLimit = Integer.parseInt(lines[0]);
    int maxWidth = lines[1].length();
    int maxHeight = lines.length - 1;
    Set<Position> destinations = new HashSet<>();
    Map<Position, Entity> map = new HashMap<>();
    Map<Character, Position> playerPositions = new HashMap<>();
    Map<Character, Integer> boxPlayerIds = new HashMap<>();

    for (int y = 0; y < maxHeight; y++) {
        String line = lines[y + 1];
        for (int x = 0; x < maxWidth; x++) {
            char symbol = line.charAt(x);
            Position position = Position.of(x, y);
            switch (symbol) {
                case '#':
                    map.put(position, new Wall());
                    break;
                case '@':
                    destinations.add(position);
                    map.put(position, new Empty());
                    break;
                case '.':
                    map.put(position, new Empty());
                    break;
                default:
                    if (Character.isUpperCase(symbol)) {
                        if (playerPositions.containsKey(symbol)) {
                            throw new IllegalArgumentException("Multiple players with the same id");
                        }
                        playerPositions.put(symbol, position);
                        map.put(position, new Player(symbol - 'A'));
                    } else if (Character.isLowerCase(symbol)) {
                        if (!playerPositions.containsKey(Character.toUpperCase(symbol))) {
                            throw new IllegalArgumentException("Box playerId does not match any player on the game board");
                        }
                        int playerId = playerPositions.get(Character.toUpperCase(symbol)).getId();
                        boxPlayerIds.put(symbol, playerId);
                        map.put(position, new Box(playerId));
                    } else {
                        throw new IllegalArgumentException("Invalid symbol in map");
                    }
            }
        }
    }

    if (playerPositions.isEmpty()) {
        throw new IllegalArgumentException("No players in the map");
    }

    if (boxPlayerIds.size() != destinations.size()) {
        throw new IllegalArgumentException("Number of boxes is not equal to the number of box destinations");
    }

    for (char playerSymbol : playerPositions.keySet()) {
        if (!boxPlayerIds.containsKey(playerSymbol)) {
            throw new IllegalArgumentException("Player has no corresponding box");
        }
    }

    return new GameMap(map, destinations, undoLimit);
}

public Entity getEntity(Position position) {
    return map.get(position);
}

public void putEntity(Position position, Entity entity) {
    map.put(position, entity);
}

public Set<Position> getDestinations() {
    return destinations;
}

public Optional<Integer> getUndoLimit() {
    if (undoLimit == -1) {
        return Optional.empty();
    } else {
        return Optional.of(undoLimit);
    }
}

public Set<Integer> getPlayerIds() {
    return map.values().stream()
            .filter(entity -> entity instanceof Player)
            .map(entity -> ((Player) entity).getId())
            .collect(Collectors.toSet());
}

public int getMaxWidth() {
    return maxWidth;
}

public int getMaxHeight() {
    return maxHeight;
}
