public static GameMap parse(String mapText) {
    String[] lines = mapText.split("\n");
    int undoLimit = Integer.parseInt(lines[0]);
    int maxWidth = lines[1].length();
    int maxHeight = lines.length - 1;
    Set<Position> destinations = new HashSet<>();
    Map<Position, Entity> map = new HashMap<>();
    Map<Character, Integer> playerIds = new HashMap<>();
    AtomicInteger boxCount = new AtomicInteger(0);

    for (int y = 0; y < maxHeight; y++) {
        String line = lines[y + 1];
        for (int x = 0; x < maxWidth; x++) {
            char symbol = line.charAt(x);
            Position position = Position.of(x, y);
            if (symbol == '#') {
                map.put(position, new Wall());
            } else if (symbol == '@') {
                destinations.add(position);
            } else if (Character.isUpperCase(symbol)) {
                int playerId = playerIds.computeIfAbsent(symbol, k -> playerIds.size() + 1);
                map.put(position, new Player(playerId));
            } else if (Character.isLowerCase(symbol)) {
                int playerId = playerIds.get(Character.toUpperCase(symbol));
                map.put(position, new Box(playerId));
                boxCount.incrementAndGet();
            } else if (symbol == '.') {
                map.put(position, new Empty());
            }
        }
    }

    if (undoLimit < -1) {
        throw new IllegalArgumentException("Undo limit must be non-negative or -1 for unlimited undo.");
    }

    if (playerIds.isEmpty()) {
        throw new IllegalArgumentException("There must be at least one player in the map.");
    }

    if (boxCount.get() != destinations.size()) {
        throw new IllegalArgumentException("The number of boxes must be equal to the number of box destinations.");
    }

    for (Entity entity : map.values()) {
        if (entity instanceof Box) {
            int playerId = ((Box) entity).getPlayerId();
            if (!playerIds.containsValue(playerId)) {
                throw new IllegalArgumentException("Box playerId does not match any player on the game board.");
            }
        }
    }

    for (int playerId : playerIds.values()) {
        if (map.values().stream().noneMatch(entity -> entity instanceof Box && ((Box) entity).getPlayerId() == playerId)) {
            throw new IllegalArgumentException("Player " + playerId + " has no corresponding box.");
        }
    }

    return new GameMap(map, destinations, undoLimit);
}

public Entity getEntity(Position position) {
    return map.getOrDefault(position, new Empty());
}

public Set<Position> getDestinations() {
    return destinations;
}

