public static GameMap parse(String mapText) {
    String[] lines = mapText.split("\n");
    int undoLimit = Integer.parseInt(lines[0]);
    int maxWidth = lines[1].length();
    int maxHeight = lines.length - 1;
    Set<Position> destinations = new HashSet<>();
    Map<Position, Entity> map = new HashMap<>();
    Map<Character, Player> players = new HashMap<>();
    Map<Character, Box> boxes = new HashMap<>();

    for (int y = 0; y < maxHeight; y++) {
        String line = lines[y + 1];
        for (int x = 0; x < maxWidth; x++) {
            char symbol = line.charAt(x);
            Position position = Position.of(x, y);
            switch (symbol) {
                case '#':
                    map.put(position, new Wall());
                    break;
                case '@':
                    destinations.add(position);
                    map.put(position, new Empty());
                    break;
                case '.':
                    map.put(position, new Empty());
                    break;
                default:
                    if (Character.isUpperCase(symbol)) {
                        Player player = new Player(symbol);
                        players.put(symbol, player);
                        map.put(position, player);
                    } else if (Character.isLowerCase(symbol)) {
                        Box box = new Box(symbol);
                        boxes.put(symbol, box);
                        map.put(position, box);
                    } else {
                        throw new IllegalArgumentException("Invalid symbol in the map: " + symbol);
                    }
                    break;
            }
        }
    }

    if (players.isEmpty()) {
        throw new IllegalArgumentException("There are no players in the map.");
    }

    if (boxes.size() != destinations.size()) {
        throw new IllegalArgumentException("The number of boxes is not equal to the number of box destinations.");
    }

    for (Box box : boxes.values()) {
        char playerId = box.getPlayerId();
        if (!players.containsKey(Character.toUpperCase(playerId))) {
            throw new IllegalArgumentException("Box with playerId " + playerId + " does not match any player on the game board.");
        }
    }

    for (Player player : players.values()) {
        char playerId = player.getId();
        if (!boxes.containsKey(Character.toLowerCase(playerId))) {
            throw new IllegalArgumentException("Player with id " + playerId + " has no corresponding box.");
        }
    }

    return new GameMap(map, destinations, undoLimit);
}

public Entity getEntity(Position position) {
    return map.get(position);
}

public void putEntity(Position position, Entity entity) {
    map.put(position, entity);
}

public Set<Position> getDestinations() {
    return destinations;
}

public int getMaxWidth() {
    return maxWidth;
}
