public static GameMap parse(String mapText) {
    String[] lines = mapText.split("\n");
    int undoLimit = Integer.parseInt(lines[0]);
    Map<Position, Entity> map = new HashMap<>();
    Set<Position> destinations = new HashSet<>();
    Map<Integer, Position> playerPositions = new HashMap<>();
    Map<Integer, Integer> boxPlayerIds = new HashMap<>();

    for (int y = 0; y < lines.length - 1; y++) {
        String line = lines[y + 1];
        for (int x = 0; x < line.length(); x++) {
            char c = line.charAt(x);
            Position position = Position.of(x, y);
            switch (c) {
                case '#':
                    map.put(position, new Wall());
                    break;
                case '@':
                    destinations.add(position);
                    map.put(position, new Empty());
                    break;
                case '.':
                    map.put(position, new Empty());
                    break;
                default:
                    if (Character.isUpperCase(c)) {
                        int playerId = c - 'A';
                        Player player = new Player(playerId);
                        map.put(position, player);
                        playerPositions.put(playerId, position);
                    } else if (Character.isLowerCase(c)) {
                        int playerId = Character.toUpperCase(c) - 'A';
                        if (!playerPositions.containsKey(playerId)) {
                            throw new IllegalArgumentException("Player " + c + " does not exist on the game board.");
                        }
                        Box box = new Box(playerId);
                        map.put(position, box);
                        boxPlayerIds.put(playerId, playerId);
                    } else {
                        throw new IllegalArgumentException("Invalid character in the map: " + c);
                    }
                    break;
            }
        }
    }

    if (playerPositions.isEmpty()) {
        throw new IllegalArgumentException("There are no players in the map.");
    }

    if (boxPlayerIds.size() != playerPositions.size()) {
        throw new IllegalArgumentException("The number of boxes is not equal to the number of box destinations.");
    }

    for (int playerId : playerPositions.keySet()) {
        if (!boxPlayerIds.containsKey(playerId)) {
            throw new IllegalArgumentException("Player " + (char) ('A' + playerId) + " has no corresponding box.");
        }
    }

    return new GameMap(map, destinations, undoLimit);
}

public Entity getEntity(Position position) {
    return map.getOrDefault(position, new Empty());
}

public void putEntity(Position position, Entity entity) {
    map.put(position, entity);
}

