        }

        if (boxPlayerIds.size() != destinations.size()) {
            throw new IllegalArgumentException("The number of boxes must be equal to the number of box destinations.");
        }

        if (!playerIds.containsAll(boxPlayerIds) || !boxPlayerIds.containsAll(playerIds)) {
            throw new IllegalArgumentException("Some boxes do not match any player or some players have no corresponding boxes.");
