     */
    public int getMapMaxHeight() {
        return boardHeight;
    }

    private static class Transition {
        private final Map<Position, Entity> entities;
        private final Transition previousTransition;
        private final int undoQuota;

        private Transition(Map<Position, Entity> entities, Transition previousTransition, int undoQuota) {
            this.entities = entities;
            this.previousTransition = previousTransition;
