        int numGems = 0;
        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                Cell cell = board[i][j];
                if (cell instanceof Gem) {
                    numGems++;
                }
            }
        }
        return numGems;
    }

    private Player findPlayer() {
        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                Cell cell = board[i][j];
                if (cell instanceof Player) {
                    return (Player) cell;
                }
            }
        }
        throw new IllegalArgumentException("No player found");
    }

    private void validate() {
        if (numRows != board.length) {
            throw new IllegalArgumentException("Number of rows does not match");
        }
        if (numCols != board[0].length) {
            throw new IllegalArgumentException("Number of columns does not match");
        }
        int numPlayers = 0;
        int numGems = 0;
        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                Cell cell = board[i][j];
                if (cell instanceof Player) {
                    numPlayers++;
                } else if (cell instanceof Gem) {
                    numGems++;
                }
            }
        }
        if (numPlayers != 1) {
            throw new IllegalArgumentException("Number of players does not match");
        }
        if (numGems == 0) {
