        boolean playerFound = false;
        int numGems = 0;
        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                Cell cell = cells[i][j];
                if (cell instanceof Player) {
                    if (playerFound) {
                        throw new IllegalArgumentException("More than one player in the game board");
                    }
                    player = (Player) cell;
                    playerFound = true;
                } else if (cell instanceof Gem) {
                    numGems++;
                }
            }
        }

        if (!playerFound) {
            throw new IllegalArgumentException("No player in the game board");
        }
        if (numGems == 0) {
            throw new IllegalArgumentException("No gems in the game board");
        }
    }

