        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                if (cells[i][j] instanceof Player) {
                    return (Player) cells[i][j];
                }
            }
        }
        return null;
    }

    private List<Gem> findGems(Cell[][] cells) {
        List<Gem> gems = new ArrayList<>();
        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                if (cells[i][j] instanceof Gem) {
                    gems.add((Gem) cells[i][j]);
                }
            }
        }
        return gems;
    }

    private List<Gem> findGems(Cell[][] cells) {
        List<Gem> gems = new ArrayList<>();
        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                if (cells[i][j] instanceof Gem) {
                    gems.add((Gem) cells[i][j]);
                }
            }
        }
        return gems;
    }

