        Pipe pipe = pipeQueue.peek();
        if (map.tryPlacePipe(coord, pipe)) {
            cellStack.push((FillableCell) map.getCell(coord));
            pipeQueue.consume();
            numOfSteps++;
            return true;
        }
        return false;
    }

    /**
