    public char toSingleChar() {
        return this.shape.getCharByState(this.filled);
    }

    public static Pipe fromString(String rep) {
        for (Shape shape : Shape.values()) {
            if (shape.filledChar == rep.charAt(0)) {
                return new Pipe(shape);
            }
        }
        throw new IllegalArgumentException("Invalid representation: " + rep);
    }

    public enum Shape {

        HORIZONTAL(PipePatterns.Filled.HORIZONTAL, PipePatterns.Unfilled.HORIZONTAL, new Direction[]{Direction.LEFT, Direction.RIGHT}),
